import { Link, useNavigate } from 'react-router-dom';
import './login.css';
import apiService from "../../services/apiService.js"




function Login() {
    let nextRoute = ''

    async function handleClickLogin(values) {
        await apiService.login({
            'login': values[''],
            'senha': values['']
        }).then(response => {
            let perfil = response.data
            if (perfil['categoria'] == 'cliente') {
                nextRoute = '/adminCliente'
            } else if (perfil['categoria'] == 'fornecedor') {
                nextRoute = '/adminFornecedor'
            } else nextRoute = '/adminFuncionario'

            const navigate = useNavigate()
            navigate(nextRoute, { replace: true })

        }).catch(e => {
            console.log(e)
        })
    }

    return (
        <div className="login">
            <div className="login-logo">
                <img src="https://estrelalocadoramg.com.br/wp-content/uploads/2019/03/frota-2.png"
                    alt="Login App"
                />
            </div>

            <div className="login-right">
                <h1>Acessar Locadora</h1>

                <div className="login-loginInputEmail">
                    <input
                        type="text"
                        placeholder="Digite um email"
                    />
                </div>


                <div className="login-loginInputPassword">
                    <input
                        type="text"
                        placeholder="Digite sua senha"
                    />
                </div>

                <button type="submit" onClick={handleClickLogin}>
                    Entrar
                </button>
                <h4> NÃ£o Possuo Conta</h4>
                <Link to="/adminCliente/reservar"> Cadastrar </Link>
                <button type="submit">
                    Cadastrar
                </button>

            </div>
        </div>
    );
}

export default Login;
